NeutronAttenuationTestMessenger::NeutronAttenuationTestMessenger( NeutronAttenuationTestDetector *Det )
  : NeutronAttenuationTestdetector( Det )
{
  //  Set up the directory
  NeutronAttenuationTestDir = new G4UIdirectory( "/NeutronAttenuationTest/" );
  NeutronAttenuationTestDir->SetGuidance( "All NeutronAttenuationTest project commands are contained under /NeutronAttenuationTest/" );
  
  ShieldingThicknessCommand = new G4UIcmdWithADoubleAndUnit( "/NeutronAttenuationTest/shieldingThickness", this );
  ShieldingThicknessCommand->AvailableForStates( G4State_PreInit, G4State_Idle );

}

//------++++++------++++++------++++++------++++++------++++++------++++++------
