//------++++++------++++++------++++++------++++++------++++++------++++++------
NeutronAttenuationTestMessenger::~NeutronAttenuationTestMessenger()
{
  //delete everything that you declared with new
  delete ShieldingThicknessCommand;
  delete NeutronAttenuationTestDir;

}

//------++++++------++++++------++++++------++++++------++++++------++++++------
//					SetNewValue()
//------++++++------++++++------++++++------++++++------++++++------++++++------
void NeutronAttenuationTestMessenger::SetNewValue( G4UIcommand *command, G4String newValue )
{
  if( command == ShieldingThicknessCommand ) 
      NeutronAttenuationTestdetector->SetShieldingThickness( G4UIcmdWithADoubleAndUnit::GetNewDoubleValue( newValue.data() )  );
}

